import {
  LocationMarkerIcon,
  PencilAltIcon,
  PencilIcon,
} from '@heroicons/react/solid';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import CatagoryButton from '../components/CategoryButton';
import EditProfile from '../components/EditProfile';
import Header from '../components/Header';
import ImageUpload from '../components/ImageUpload';
import {
  endorseUser,
  getGivenEndorsements,
  getReceivedEndorsements,
} from '../store/endorsement/api.endorsement';
import {
  hideEdit,
  hideImageUpload,
  unhideDivImageUpload,
  unhideEdit,
} from '../store/ui/hideDiv';
import { getUserDetails, updateUserProfile } from '../store/user/api.user';
import { capitalizeFirstLetter } from '../util/firstLetterCapitalizer';

function Profile() {
  const dispatch = useDispatch();

  const { id } = useParams();

  const { currentUser } = useSelector((state) => state.userLogin);
  const { loading, user, error } = useSelector((state) => state.userDetail);
  const { givenEndorsements } = useSelector((state) => state.givenEndorsements);
  const { receivedEndorsements } = useSelector(
    (state) => state.receivedEndorsements
  );
  useEffect(() => {
    dispatch(getUserDetails(id));
    dispatch(hideImageUpload());
    dispatch(hideEdit());
  }, []);

  const endorseHandler = () => {
    dispatch(endorseUser({ endorseeId: id }));
  };

  useEffect(() => {
    if (user?._id !== currentUser._id && user?.role !== 'PRO_DIRECTOR')
      dispatch(getGivenEndorsements());
  }, []);
  useEffect(() => {
    if (user?.role !== 'PRO_DIRECTOR') dispatch(getReceivedEndorsements(id));
  }, []);
  console.log(receivedEndorsements);
  const unhide = () => {
    dispatch(unhideDivImageUpload());
  };

  const { hidden } = useSelector((state) => state.hideImageUpload);
  const hiiideEdit = useSelector((state) => state.hideEdit);

  return (
    <div>
      <Header />
      <div className="border relative lg:max-w-5xl mx-auto mt-5">
        <div
          className={
            'bg-transparent items-center justify-center ' +
            hidden +
            ' absolute inset-x-0 inset-y-0 z-10'
          }
        >
          <ImageUpload />
        </div>
        <div
          className={
            'bg-transparent items-center justify-center ' +
            hiiideEdit.hidden +
            ' absolute inset-x-0 inset-y-0 z-10'
          }
        >
          <EditProfile />
        </div>
        <div className="relative flex items-center py-4 px-5 md:mx-10 md:my-5 border-b-2 w-full">
          <div className="relative">
            <img
              className="rounded-full h-20 w-20"
              alt=""
              src={
                user?.avatar ??
                'data:image/jpeg;base64,'
              }
            />
            {user?._id === currentUser?.id ?? (
              <div
                className="absolute top-1 h-6 w-6 rounded-full bg-white flex items-center justify-center"
                onClick={() => unhide()}
              >
                <PencilIcon className="h-4  " />
              </div>
            )}
          </div>
          <div className="items-center pl-4  sm:pl-8">
            <h1 className="text-2xl">{user?.fullName}</h1>
            <p className="flex text-sm items-center">
              <LocationMarkerIcon className="h-4 pr-1 text-blue-600" />{' '}
              {user?.address?.region ?? user?.address?.city},{' '}
              {user?.address?.country === 'ET'
                ? 'Ethiopia'
                : user?.address?.country}
            </p>
          </div>
        </div>{' '}
        <div className="py-3 px-5 space-y-2 border-b-2">
          {user?._id === currentUser?.id && (
            <div className=" flex items-center space-x-6 ">
              <h2 className="text-xl">Edit Profile</h2>
              <div
                className="rounded-full border-2 flex items-center justify-center h-8 w-8"
                onClick={() => dispatch(unhideEdit())}
              >
                <PencilIcon className="h-5" />
              </div>
            </div>
          )}
          <div className="space-y-4">
            <h2 className="text-xl font-bold">
              {capitalizeFirstLetter(user?.talent)}
            </h2>
            <p className="font-light text-sm md:text-base">
              {user?.bio ?? user?._id === currentUser?.id
                ? 'Your bio is empty'
                : ''}
            </p>
          </div>
        </div>
        <div className="py-3 px-5 border-b-2">
          <h2 className="font-bold">Other Talents</h2>
          <div className="mt-1">
            {user?.otherTalents?.map((talent) => (
              <CatagoryButton text="talent" />
            ))}
          </div>
        </div>
        <div className="">
          <h2 className="font-bold text-xl pt-3  px-5">Personal Information</h2>
          <div className="flex justify-between pb-2 px-5">
            <div className="flex items-center space-x-4">
              <h3>Email:</h3>
              <p className="font-light">{user?.email}</p>
            </div>
          </div>
          <div className="pb-2 px-5 ">
            <div className="flex justify-between">
              <h3>Location</h3>
            </div>
            <div className="flex pl-3 space-x-4 text-sm">
              <h4>Country:</h4>
              <p className="font-light">
                {user?.address?.country === 'ET' && 'Ethiopia'}
              </p>
            </div>
            <div className="flex pl-3 space-x-4 text-sm">
              <h4>Region:</h4>
              <p className="font-light">{user?.address?.region}</p>
            </div>
          </div>
          <div className="flex justify-between pb-2 px-5 border-b-2">
            <div className="flex items-center space-x-4">
              <h3>Gender:</h3>
              <p className="font-light">
                {capitalizeFirstLetter(user?.gender)}
              </p>
            </div>
          </div>
        </div>
        <div className="py-3 px-5 border-b-2 flex justify-between">
          <div className="">
            <h2 className="font-bold">Endorsements</h2>
            {receivedEndorsements?.length === 0 ? (
              <p class="text-sm font-light">Endorsements from past clients</p>
            ) : (
              receivedEndorsements?.map((received) => (
                <p>{received?.endorserId?.fullName}</p>
              ))
            )}
          </div>
        </div>
        {/* <div className="py-3 px-5 border-b-2 flex justify-between">
          <div className="">
            <h2 className="font-bold">Certifications</h2>
            <p className="text-sm font-light">
              Add certifications to highlight your best skills
            </p>
          </div>
          <button className="border-2 h-8 w-8 flex justify-center items-center rounded-full">
            <p className="text-2xl font-light">+</p>
          </button>
        </div> */}
      </div>
      {givenEndorsements?.some(
        (given) => given.endorseeId.toString() === id.toString()
      ) ? (
        <button
          className="flex mx-auto py-2 px-4 rounded-full bg-red-500"
          // onClick={() => dispatch(endorseDele)}
        >
          remove Endorse
        </button>
      ) : (
        <button
          className="flex mx-auto py-2 px-4 rounded-full bg-green-500"
          onClick={() => endorseHandler()}
        >
          Endorse
        </button>
      )}
    </div>
  );
}

export default Profile;
